import React, { useEffect, useState } from 'react';
import '../styles/TcpHandshakeAnimation.css';

const TcpHandshakeAnimation = () => {
    const [step, setStep] = useState(0);
    const [handshakeComplete, setHandshakeComplete] = useState(false);

    useEffect(() => {
        if (step < 3) {
            const timer = setTimeout(() => {
                setStep(step + 1);
            }, 1500);
            return () => clearTimeout(timer);
        } else {
            setHandshakeComplete(true);
        }
    }, [step]);

    return (
        <div className="tcp-handshake-animation">
            <h2>TCP 3-Way Handshake</h2>
            <div className="handshake-container">
                <div className="figure client">
                    <div className="figure-head">Client</div>
                    <div className="figure-body">
                        <div className="figure-torso">🧑‍💻</div>
                        <div className={`figure-arm ${step >= 0 ? 'arm-extended' : ''}`}></div>
                        <div className="figure-hand">✋</div>
                    </div>
                </div>

                <div className="message-container">
                    {step >= 0 && (
                        <div className={`message syn ${step > 0 ? 'received' : 'sending'}`}>
                            SYN
                        </div>
                    )}
                    {step >= 1 && (
                        <div className={`message syn-ack ${step > 1 ? 'received' : 'sending'}`}>
                            SYN-ACK
                        </div>
                    )}
                    {step >= 2 && (
                        <div className={`message ack ${step > 2 ? 'received' : 'sending'}`}>
                            ACK
                        </div>
                    )}
                </div>

                <div className="figure server">
                    <div className="figure-head">Server</div>
                    <div className="figure-body">
                        <div className="figure-torso">🖥️</div>
                        <div className={`figure-arm ${step >= 1 ? 'arm-extended' : ''}`}></div>
                        <div className="figure-hand">✋</div>
                    </div>
                </div>
            </div>

            <div className="handshake-status">
                {step === 0 && <p>Client sendet SYN...</p>}
                {step === 1 && <p>Server antwortet mit SYN-ACK...</p>}
                {step === 2 && <p>Client bestätigt mit ACK...</p>}
                {handshakeComplete && (
                    <div className="celebration">
                        <p>🎉 Verbindung hergestellt! 🎉</p>
                        <p className="technical-note">TCP-Verbindung erfolgreich aufgebaut.</p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default TcpHandshakeAnimation;
